export class FeatureB extends HTMLElement {
  constructor() {
    super();
    this.attachShadow({ mode: 'open' });
  }

  connectedCallback() {
    this.shadowRoot.innerHTML = `
      <style> :host { display: block; padding: 5px; background: #8478e9; }</style>
      <p>Feature B 1.x</p>
    `;
  }
}
