export class FeatureA extends HTMLElement {
  constructor() {
    super();
    this.attachShadow({ mode: 'open' });
  }

  connectedCallback() {
    this.shadowRoot.innerHTML = `
      <style> :host { display: block; padding: 5px; background: #b7e57d; }</style>
      <p>Feature A 1.x</p>
    `;
  }
}
