import { render } from 'lit-html';
import { createScope } from '../../../../index.js';
import { FeatureA } from 'feature-a/index.js';
import { FeatureB } from 'feature-b/index.js';

const { html } = createScope({
  'feature-a': FeatureA,
  'feature-b': FeatureB,
});

export class PageA extends HTMLElement {
  constructor() {
    super();
    this.attachShadow({ mode: 'open' });
  }

  connectedCallback() {
    render(html`
      <style>:host { display: block; padding: 10px; border: 2px solid #ccc; }</style>
      <h3>I am page A</h3>
      <feature-a></feature-a>
      <feature-b></feature-b>
    `, this.shadowRoot);
  }
}
