export class FeatureA extends HTMLElement {
  constructor() {
    super();
    this.attachShadow({ mode: 'open' });
  }

  connectedCallback() {
    this.shadowRoot.innerHTML = `
      <style> :host { display: block; padding: 5px; background: #e57d7d; }</style>
      <p>!! Feature A 2.x</p>
    `;
  }
}
